import Navbar from './Components/Navbar/Navbar';
import Main from './Pages/Main';
import './index.css';
import React from 'react';

import axios from 'axios';
function App() {
  // axios.post('https://localhost:5000/getItems').then((data) => {
  //   console.log(data);
  // });

  React.useEffect(() => {
    async function fetchData() {
      try {
        const [cartResponse, favoritesResponse, itemsResponse] = await Promise.all([
          axios.get('https://62c811690f32635590d09782.mockapi.io/cart'),
          axios.get('https://62c811690f32635590d09782.mockapi.io/favorites'),
          axios.get('https://62c811690f32635590d09782.mockapi.io/items'),
        ]);

        setIsLoading(false);
        setCartItems(cartResponse.data);
        setFavorites(favoritesResponse.data);
        setItems(itemsResponse.data);
      } catch (error) {
        alert('error');
      }
    }
    fetchData();
  }, []);
  return (
    <div className="wrapper">
      <Navbar />
      <Main />
    </div>
  );
}

export default App;
