import React from 'react';
import { Route, Routes } from 'react-router-dom';

import Navbar from './Components/Navbar/Navbar';
import Main from './Pages/main/Main';
import Register from './Pages/register/Register';
import Login from './Pages/login/register/Login';
import Dish from './Pages/dish/Dish';

import {TokenContext} from './TokenContext'

import './index.css';

import axios from 'axios';


function App() {
  const [dishes, setDishes] = React.useState([]);
  const [token, setToken] = React.useState("")

  const [value, setValue] = React.useState("hello from context")
  const providerValue = React.useMemo(() => ({value, setValue}), [value, setValue])

  React.useEffect(() => {
    async function fetchData() {
      try {
        const [itemsResponse] = await Promise.all([
          axios.get('http://localhost:4000/api/topDishes'),
        ]);

        setDishes(itemsResponse.data.dishes);
      } catch (error) {
        console.log(error);
      }
    }
    fetchData();
  }, []);
  console.log(dishes);

  function testToken(){
    console.log(token);
  }

  return (
    <TokenContext.Provider value={providerValue}>
      <div className="wrapper">
        <Navbar token={token} />
        <button onClick={()=>testToken()}>test token</button>
        <Routes>
          <Route exact path="/" element={<Main dishes={dishes} />}></Route>
          <Route exact path="/register" element={<Register />}></Route>
          <Route exact path="/login" element={<Login token={token} setToken={setToken} />}></Route>
          <Route exact path='/dish' element={<Dish token={token}/>}></Route>
        </Routes>

      </div>
    </TokenContext.Provider>
  );
}

export default App;
