            user_id:user_id,
        },
        {headers:{
            "Authorization": `Bearer ${localStorage.getItem("accessToken")}`
        }})
    }

    const testDish = {
        title:"TestTitle",//Featured Meal
        description: "testDescription",//Choice of: Coke, Fanta, Sprite, Upgrade to large fries, Add whopper patty, Add Tender crisp patty and more...
        price: 500,
        rating: 2
    }
    return(
        <div className="dish-wrapper">
            <div className="dish-info-block">
                <div className="dish-img-block">
                    <img src="/img/dishes/dish_1.png" alt="meal" />
                </div>
                <div className="dish-info-properties">
                    <h3>{testDish.title}</h3>
                    <p>{testDish.description}</p>
                    <p>Price: {testDish.price}$</p>
                    <p>Rating: {testDish.rating}</p>
                    <button onClick={()=>{makeOrder()}}>Order</button>
                </div>
            </div>
        </div>
    )

}

export default Dish
